 components:{
    AddJobDialog,
    SuccessSuspendJobDialog,
    SuccessRepublishJobDialog,
    DeleteJobDialog,
    SectionActionsVisiter,
    SectionActionsOwner,
    SectionDetails,
    SectionApply,
    sectionRequirement
 },
 data:()=>{
    return {
        isOwner:false,
        loading:true,
        hasError:false,
        itemPage:{}
    }
 },
 methods:{
    suspend(val){
        console.log('susp',val)
                if(val){
                    this.fireOpenDialog('success-suspend-job',this.itemPage)
                }else{
                    this.fireOpenDialog('success-republish-job',this.itemPage)

                }
            this.itemPage.is_suspend=val;
        },
    async initializing(){
        this.loading = true;
        this.hasError = false;
        try {
            let { data } = await jobsProviderAPIs.getItem(this.$route.params.id)
            if(data.success){
                this.itemPage = data.data
                this.isOwner = this.itemPage.user_info.id==this.user.id
                if(!Object.hasOwnProperty.call(this.itemPage,'is_suspend')){
                    this.itemPage.is_suspend = 0;
                }
            }else{
                this.hasError = true;
            }
        } catch (error) {
            console.log('error',error)
            this.hasError = true;
        }
        this.loading = false;
    }
 },
 mounted(){
    this.initializing()
 }
}
</script>

