<script>
import ConfirmFinishRequest from './dialogs/confirm-finish-request.vue'
import ChangePriceRequest from './dialogs/change-value-amount.vue'
import requesrPurchaseAPI from '@/services/api/service-provider/provider/my-request.js'

export default {
 name:'request-purchase-page-in-progress',
 components:{
    ConfirmFinishRequest,
    ChangePriceRequest
 },
  data:()=>{
    return {
      loading:true,
      hasError:false,
      msgError:null,
      itemPage:{},
      readyService:{},
      colors:['#F2631C','#FFBC00','#2C98B3']
  }},
  methods:{
    periodToText(period){
        switch (period) {
            case 'time_available_product': return ' التسيلم عند توفر المنتج';
            case 'specific_date': return `التسيلم يوم ${this.dateReverse(this.item.specific_date)}`;
            default:
                return 'N/A'
        }
    },
    openChangeDateDeliveryDialog(evt){
        if(evt) evt.preventDefault();
        
        this.fireOpenDialog('change-value-amount',this.itemPage)
    },
    openChangePriceDialog(evt){
        if(evt) evt.preventDefault();
        
      this.fireOpenDialog('change-value-amount',this.itemPage)
    },
    openConfirmFinishDialog(evt){
        if(evt) evt.preventDefault();
        
      this.fireOpenDialog('confirm-finish-request',this.itemPage)
    },
    async initializing() {
      this.loading = true;
      this.hasError = false;
      this.msgError = null;
            try {
                let { data } = await requesrPurchaseAPI.getItem(this.$route.params.id)
                if (data.success) {
                    let {service,...item} =data.data
                   this.itemPage = item;
                   this.readyService = service;
                }else{
                  this.hasError = true;
                  this.msgError = data.message
                }
            } catch (error) {
                console.log('error', error)
                console.log('error response', error.response)
                this.hasError = true;
                this.msgError = 'هناك خطأ غير معروف يرجي تحديث الصفحة'
              }

            this.loading = false;
        }
  },
  mounted(){
    this.initializing()
  }
}
</script>

